#include "Sysled.H"//包含头文件，系统灯有关4                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                       



//系统灯，频率1hz，既0.5亮，0.5灭
void Sysled_Display(void)
{
	static unsigned char Time500ms_Cnt = 0;//用于计算500ms的时间变量，计50个即可
	Time500ms_Cnt ++;
	if(Time500ms_Cnt >=50)
	{
		Time500ms_Cnt = 0;
		
		P45 = !P45;//系统灯取反
 	}
	
	
}